//brackets is a string of "<" and ">".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("<")
// false
// >>> correct_bracketing("<>")
// true
// >>> correct_bracketing("<<><>>")
// true
// >>> correct_bracketing("><<>")
// false
function correct_bracketing(brackets){ 
  let stack = [];

  for (let i = 0; i < brackets.length; i++) {
    const char = brackets[i];

    // If the character is an opening bracket, push it onto the stack
    if (char === '<') {
      stack.push(char);
    } else if (char === '>') {
      // If there are no opening brackets on the stack or the top of the stack is not an opening bracket,
      // then the brackets are not correctly balanced. Return false immediately.
      if (!stack.length || stack[stack.length - 1] !== '<') {
        return false;
      }

      // Pop the last element from the stack as it matches the current closing bracket
      stack.pop();
    }
  }

  // If the stack is empty after processing all characters, then all opening brackets have been matched with closing brackets.
  // Otherwise, they haven't been matched properly, so return false.
  return stack.length === 0;
}

