// true
function monotonic(l){ 
    let flag = l[0] < l[l.length-1];
    for(let i=1; i<l.length; i++){
        if(flag && (l[i] > l[i-1])) return false;
        else if(!flag && (l[i] < l[i-1])) return false;
    }
    return true;
}<|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|>

