// "db1111db"
// >>> decimal_to_binary(32)
// "db100000db"
function decimal_to_binary(decimal){ 
    let result = '';
    while (decimal > 0) {
        if (decimal % 2 === 0) {
            result = '0' + result;
        } else {
            result = '1' + result;
        }
        decimal = Math.floor(decimal / 2);
    }
    return `db${result}db`;
}

