// 9
// >>> prod_signs([0, 1])
// 0
// >>> prod_signs([])
// undefined
function prod_signs(arr){ 
    if (arr.length === 0) {
        return undefined;
    }

    let sign = 1; // Initialize sign variable

    for(let i=0; i<arr.length; i++){
        if(arr[i] < 0){
            sign *= -1; // Change sign when encountering a negative number
        } else if(arr[i] > 0){
            continue; // Skip positive numbers as their magnitude is already considered
        } else { // If zero encountered
            sign = 0; // Set sign to 0 since any number multiplied by 0 equals 0
            break; // Exit loop early as no further processing needed
        }
    }

    let result = 0;
    for(let j=0; j<arr.length; j++){
        result += Math.abs(arr[j]) * sign; // Multiply absolute value with current sign
    }

    return result;
}<|endoftext|>

