// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){ // O(n)
    let stack = [];
    for (let i = 0; i < string.length; i++) {
        if (string[i] === '[') {
            stack.push(i);
        } else if (string[i] === ']') {
