    if (n % 2 != 0) {
        return false;
    }
    for(let i = 1; i <= Math.sqrt(n); i++) {
        let j = n - i * 2;
        if(j % 2 == 0 && Math.floor(Math.sqrt(j)) ** 2 === j){
