// - The substring before the dot should not be empty, and it starts with a letter from 
// the latin alphapet ('a'-'z' and 'A'-'Z').
// - The substring after the dot should be one of these: ['txt', 'exe', 'dll']
// Examples:
// >>> file_name_check("example.txt")
// "Yes"
// >>> file_name_check("1example.dll")
// "No"
function file_name_check(file_name){  
    //check for at least 3 digits
    const hasMoreThanThreeDigits = /\d{4}/.test(file_name);
    if(hasMoreThanThreeDigits){
        return "No";
    }

    //check for exact one dot
    const hasOneDot = /\./.test(file_name);
    if(!hasOneDot){
        return "No";
    }

    //check for first part of filename
    const isFirstPartLetter = /^[a-zA-Z]/.test(file_name.slice(0,-4));
    if(!isFirstPartLetter){
        return "No";
    }

    //check for second part of filename
    const isSecondPartValid = ["txt", "exe", "dll"].includes(file_name.slice(-4).toLowerCase());
    if(!isSecondPartValid){
        return "No";
    }

    return "Yes";
}<|endoftext|>

